import { useState } from "react"


function Form({transactions,setTransactions}){

    const [formData,setFormData]=useState({

        date:"",
        description:"",
        category:"",
        amount:""

    }) 
    function Submit(event){
        event.preventDefault()
        setFormData([...transactions,formData])

    }

    function Change(event){
     setFormData(
        {

        ...formData,
        [event.target.name] : event.target.value
     }
    )
    }

    return(
    <>
    <form onSubmit={Submit}>
        <div className="row border m-4 p-4">
    <div className="col-3 mx-auto"><input className="form-control col-2" onChange={Change} name="date" type="date"  value={formData.date}placeholder="Date" aria-label=".form-control-sm example"/></div>
    <div className="col-3 mx-auto"><input className="form-control col-2" onChange={Change} name="description" type="text"  value={formData.description}placeholder="Description" aria-label=".form-control-sm example"/></div> 
    <div className="col-3 mx-auto"><input className="form-control col-2" onChange={Change} name="category" type="text"  value={formData.category}placeholder="Category" aria-label=".form-control-sm example"/></div>
    <div className="col-3 mx-auto"><input className="form-control col-2" onChange={Change} name="amount" type="number"  value={formData.amount}placeholder="Amount" aria-label=".form-control-sm example"/></div>
    <div className="col-3 mx-auto m-2 p-2">
        <button type="submit" className="mx-auto btn btn-warning">Add Transaction</button>
    </div>
    </div>
    </form>

    </>
    )




}





export default Form
