import Row from "./Row"
import { useState } from "react"









function Table(){
  const [transactions,setTransactions]=useState(newTransaction
  )

  function Change(e){
    const alter=e.target.value
    if (alter.length>0){

    const filter=transactions.filter((transaction)=>{
     return transaction.description.toLowerCase().includes(alter.toLowerCase())
    })

    setTransactions([...filter])
   }
   else{
    setTransactions([...newTransaction])
   }
  }


return(
    <>
      <table className="table table-light">
        <thead>
        <tr>
          <th scope="col-3 mx-auto">Date</th>
          <th scope="col-3 mx-auto">Description</th>
          <th scope="col-3 mx-auto">Category</th>
          <th scope="col-3 mx-auto">Amount</th>
         </tr>
        </thead>
        <tbody>
            <Row transaction={transactions}/>
        </tbody>
    </table>
    </>
)

}

export default Table
