import Table from './Components/Table';
import './App.css';
import SearchTransaction from './Components/Search';
import { useState } from 'react';

function App() {
  const newTransaction=[
    {
      date:"2019/12/01",
      description:"Paycheck",
      category:"Income",
      amount:"1000",
       },

     {
     date:"2019/09/20",
     description:"Pizza",
     category:"Food",
     amount:"250",
     },
     {
     date:"2023/10/26",
     description:"Sunglasses",
     category:"Fashion",
     amount:"100",
     },
     {
     date:"2024/07/09",
     description:"Games",
     category:"Entertainment",
     amount:"150",
     },

     ]





const [transactions,setTransactions]=useState(newTransaction)

function Change(e){
  const alter=e.target.value
  if (alter.length>0){

  const filter=transactions.filter((transaction)=>{
   return transaction.description.toLowerCase().includes(alter.toLowerCase())
  })

  setTransactions([...filter])
 }
 else{
  setTransactions([...newTransaction])
 }
}

  return (
  <div className='container'>
  <SearchTransaction/>
  <Table transactions={transactions}/>
  </div>
  );

}
export default App;
