import Row from "./Row"
import { useState } from "react"




    function Change(e){
     const alter=e.target.value
     const filter=transactions.filter((transaction)=>{
      return transaction.description === alter
     })
     transactions= [...filter]
    }



function Table(){
  const [transactions,setTransactions]=useState([
    {
      date:"2019/12/01",
      description:"Paycheck",
      category:"Income",
      amount:"1000",
       },

     {
     date:"2019/09/20",
     description:"Pizza",
     category:"Food",
     amount:"250",
     },
     {
     date:"2023/10/26",
     description:"Sunglasses",
     category:"Fashion",
     amount:"100",
     },
     {
     date:"2024/07/09",
     description:"Games",
     category:"Entertainment",
     amount:"150",
     },

     ]


  )


return(
    <><div className="m-4 p-4">
      <input onChange={Change} className="form-control form-control-sm"type="search" placeholder="Search Transaction"/>
    </div>
      <table className="table table-light">
        <thead>
        <tr>
          <th scope="col-3 mx-auto">Date</th>
          <th scope="col-3 mx-auto">Description</th>
          <th scope="col-3 mx-auto">Category</th>
          <th scope="col-3 mx-auto">Amount</th>
         </tr>
        </thead>
        <tbody>
            <Row money={transactions}/>
        </tbody>
    </table>
    </>
)

}

export default Table
