import { useState } from "react"


function Form({transactions,setTransactions}){

    const [formEntry,enterFormData]={useState}({

        date:"",
        description:"",
        category:"",
        amount:"",

    })
    return(
    <>
    <form onSubmit={Submit}>
        <div className="row border m-4 p-4">
    <div className="col-3 mx-auto"><input className="form-control col-2" onChange={Change} name="date" type="date"  value={formEntry.date}placeholder="Date" aria-label=".form-control-sm example"/></div>
    <div className="col-3 mx-auto"><input className="form-control col-2" onChange={Change} name="description" type="text"  value={formEntry.description}placeholder="Description" aria-label=".form-control-sm example"/></div> 
    <div className="col-3 mx-auto"><input className="form-control col-2" onChange={Change} name="category" type="text"  value={formEntry.category}placeholder="Category" aria-label=".form-control-sm example"/></div>
    <div className="col-3 mx-auto"><input className="form-control col-2" onChange={Change} name="Amount" type="number"  value={formEntry.amount}placeholder="Amount" aria-label=".form-control-sm example"/></div>
    <div className="col-3 mx-auto m-2 p-2">
        <button type="submit" className="mx-auto btn btn-warning">Add Transaction</button>
    </div>
    </div>
    </form>

    </>
    )


function Submit(e){
    e.preventdefault()
    enterFormData([...transactions,formEntry])
}

function Change(e){
 enterFormData(
    {
        ...formEntry,
        [e.target.name]:e.target.value
    }
 )
}

}





export default Form
